		let projection = "EPSG:" + options.geometry.match(/SRID=(.*?);/)[1];
		let wkt = options.geometry.replace(/SRID=(.*?);/, '');

		let format = new WKT();
		let feature = format.readFeature(wkt);
		options.values.geometry = feature.getGeometry().transform(projection, view.getProjection().getCode());
		source.addFeature(new Feature(options.values));
		self.finished(pid,self.queue.DEFINE.FIN_OK);

	}
	/**
	 * Zoom a layer to the extent of its features (needs appropriate zoom levels to work well
	 * @param pid
	 * @param json
	 * @param {string} json.map - Map reference
	 * @param {string} json.layer - Layer to get extent from
	 * @example
	 * openlayers.zoomToLayerExtent({"map":"map_1","layer":"data"});
	 */
	zoomToLayerExtent(pid,json) {
		let self=this;
		let options=Object.assign({
			"map":"default",
			"layer":"default"
		},json);
		/*
		 * Pull all our resources
		 */
		let map=self.maps[options.map].object;
		let view = map.getView();
		let layer=self.maps[options.map].layers[options.layer];
		let source = layer.getSource();
		/*
		 * Get the extent of the features and fit them
		 */
		let extent = source.getExtent();
		view.fit(extent, map.getSize());

		self.finished(pid,self.queue.DEFINE.FIN_OK);
	}
}

/** @module ourthings/Queueable/Templates */
import Queueable from "../Queueable";

/**
 * @classdesc
 *
 * Template manipulations
 *
 * @author Richard Reynolds richard@nautoguide.com
 *
 * @example
 * templates.render({"targetId":"content","template":"basic"});
 *
 */
class Templates extends Queueable {

	/**
	 * Render a template into the dom using the queues templateProcessor
	 * @param {number} pid - Process ID
	 * @param {object} json - queue arguments
	 * @param {string} json.template - dom id of template to use
	 * @param {string} [json.target] - dom id of render target
	 * @example
	 * templates.render({"targetId":"content","template":"basic"});
	 */
	render(pid,json) {
		let self=this;
		self.set(pid,json);
		if(!self.queue.templateProcessor(json.template,json.targetId))
			self.finished(pid,self.queue.DEFINE.FIN_ERROR,'Could not render template');
		else
			self.finished(pid,self.queue.DEFINE.FIN_OK);
	}
}

export default Templates;

/** @module ourthings/Queueable */

/**
 * @classdesc
 *
 * The base class for queueable things
 *
 * @author Richard Reynolds richard@nautoguide.com
 *
 * @example
 * // queue = new Queue();
 *
 */
class Queueable {

	/**
	 * Constructor, Sets our status to be false (flipped on init)
	 *
	 */
	constructor() {
		let self=this;
		self.ready=false;
	}

	/**
	 * init, override this for any promise based inits setting
	 * the self.ready=true after the promise
	 *
	 */
	init(queue) {
		let self=this;
		self.queue=queue;
		self.ready=true;
	}

	/**
	 * Called from queue, starts running the actual command
	 * @param pid
	 * @param command
	 * @param json
	 */
	start(pid,command,json) {
		let self=this;
		if(self[command]&&typeof self[command] === 'function') {

			/*
			 * Pass the json through the var processor
			 */

			json=JSON.parse(self.queue.templateVars(JSON.stringify(json)));
			/*
			 * Execute
			 */
			self[command](pid, json);
		} else {
			self.queue.finished(pid,self.queue.DEFINE.FIN_ERROR,'No such command ['+command+']');
		}
	}

	/**
	 * Call this method after you command has finished. Failure to do so will result is
	 * a stalled queue
	 * @param pid
	 * @param mode
	 */
	finished(pid,mode,error='') {
		let self=this;
		self.queue.finished(pid,mode,error);
	}

	set(pid,value) {
		let self=this;
		self.queue.memory(pid,value);
	}
}

export default Queueable;
